import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="container">
    <div class="content-wrapper">
        <mat-form-field appearance="fill">
            <textarea matInput cdkTextareaAutosize cdkAutosizeMaxRows="5" placeholder="Ask your question..."></textarea>

        </mat-form-field>
        <div class="new-post-action-container">
            <button mat-raised-button class ="post-button">POST</button>
        </div>
    </div>
</div>
