import { Component } from '@angular/core';
import { QuestionService } from 'src/app/services/question.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
constructor(public questionservice :QuestionService ,public userservice: UserService){

}
  question:string= "";
  post(){
    this.questionservice.postQuestion({
      username :this.userservice.user.username,
      question :this.question,
      solutions: []
    }).subscribe((res)=>{
      console.log(res);
      console.log("post question");
    })
  }
}
<div class="container">
    <div class="content-wrapper">
        <mat-form-field appearance="fill">
            <textarea  [(ngModel)] = "question" matInput cdkTextareaAutosize cdkAutosizeMaxRows="5" placeholder="Ask your question..."></textarea>

        </mat-form-field>
        <div class="new-post-action-container">
            <button (click) ="post()" mat-raised-button [disabled] = "question.trim() == ''" class ="post-button">POST</button>
        </div>

        <mat-card class="question-card">
            <mat-card-header>
                <mat-card-title>Charan</mat-card-title>
            </mat-card-header>
            <mat-card-content>
                <p>is this my  question?/p>
            </mat-card-content>
        </mat-card>
    </div>
</div>
